export const generateSortingSteps = (algorithm, arr) => {
  switch (algorithm) {
    case '선택 정렬': return selectionSort(arr);
    case '삽입 정렬': return insertionSort(arr);
    case '버블 정렬': return bubbleSort(arr);
    case '병합 정렬': return mergeSort(arr);
    case '힙 정렬': return heapSort(arr);
    case '퀵 정렬': return quickSort(arr);
    default: return [];
  }
};

export const selectionSort = (arr) => {
  const steps = [];
  const n = arr.length;

  for (let i = 0; i < n - 1; i++) {
    let minIdx = i;
    steps.push({
      array: [...arr],
      description: `${i+1}번째 패스: 인덱스 ${i}부터 최소값을 찾기 시작합니다.`,
      variables: { i, minIdx }
    });

    for (let j = i + 1; j < n; j++) {
      if (arr[j] < arr[minIdx]) {
        minIdx = j;
        steps.push({
          array: [...arr],
          description: `새로운 최소값 ${arr[minIdx]}을(를) 인덱스 ${minIdx}에서 찾았습니다.`,
          variables: { i, j, minIdx },
          activeIndices: [i, j, minIdx]
        });
      }
    }

    if (minIdx !== i) {
      [arr[i], arr[minIdx]] = [arr[minIdx], arr[i]];
      steps.push({
        array: [...arr],
        description: `${arr[i]}과(와) ${arr[minIdx]}을(를) 교환했습니다.`,
        variables: { i, minIdx },
        activeIndices: [i, minIdx]
      });
    } else {
      steps.push({
        array: [...arr],
        description: `${arr[i]}이(가) 이미 올바른 위치에 있습니다.`,
        variables: { i, minIdx }
      });
    }
  }

  steps.push({
    array: [...arr],
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};

export const insertionSort = (arr) => {
  const steps = [];
  const n = arr.length;

  for (let i = 1; i < n; i++) {
    let key = arr[i];
    let j = i - 1;

    steps.push({
      array: [...arr],
      description: `${i}번째 원소 ${key}를 정렬된 부분에 삽입하기 시작합니다.`,
      variables: { i, key, j },
      activeIndices: [i]
    });

    while (j >= 0 && arr[j] > key) {
      arr[j + 1] = arr[j];
      j = j - 1;

      steps.push({
        array: [...arr],
        description: `${arr[j+1]}을(를) 오른쪽으로 이동시킵니다.`,
        variables: { i, key, j },
        activeIndices: [j, j+1]
      });
    }

    arr[j + 1] = key;
    steps.push({
      array: [...arr],
      description: `${key}를 인덱스 ${j+1}에 삽입했습니다.`,
      variables: { i, key, j },
      activeIndices: [j+1]
    });
  }

  steps.push({
    array: [...arr],
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};

export const bubbleSort = (arr) => {
  const steps = [];
  const n = arr.length;

  for (let i = 0; i < n - 1; i++) {
    let swapped = false;

    steps.push({
      array: [...arr],
      description: `${i+1}번째 패스를 시작합니다.`,
      variables: { i }
    });

    for (let j = 0; j < n - i - 1; j++) {
      steps.push({
        array: [...arr],
        description: `${arr[j]}와(과) ${arr[j+1]}을(를) 비교합니다.`,
        variables: { i, j },
        activeIndices: [j, j+1]
      });

      if (arr[j] > arr[j + 1]) {
        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
        swapped = true;

        steps.push({
          array: [...arr],
          description: `${arr[j]}와(과) ${arr[j+1]}을(를) 교환했습니다.`,
          variables: { i, j },
          activeIndices: [j, j+1]
        });
      }
    }

    if (!swapped) {
      steps.push({
        array: [...arr],
        description: "이번 패스에서 교환이 없었으므로 정렬이 완료되었습니다.",
        variables: { i }
      });
      break;
    }
  }

  steps.push({
    array: [...arr],
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};

export const mergeSort = (arr) => {
  const steps = [];

  const merge = (left, right, start) => {
    let result = [];
    let leftIndex = 0;
    let rightIndex = 0;

    while (leftIndex < left.length && rightIndex < right.length) {
      if (left[leftIndex] < right[rightIndex]) {
        result.push(left[leftIndex]);
        leftIndex++;
      } else {
        result.push(right[rightIndex]);
        rightIndex++;
      }

      steps.push({
        array: [...arr.slice(0, start), ...result, ...left.slice(leftIndex), ...right.slice(rightIndex), ...arr.slice(start + left.length + right.length)],
        description: `${result[result.length-1]}을(를) 병합된 배열에 추가했습니다.`,
        variables: { leftIndex, rightIndex },
        activeIndices: [start + result.length - 1]
      });
    }

    return result.concat(left.slice(leftIndex)).concat(right.slice(rightIndex));
  };

  const mergeSortHelper = (arr, start = 0) => {
    if (arr.length <= 1) return arr;

    const mid = Math.floor(arr.length / 2);
    const left = arr.slice(0, mid);
    const right = arr.slice(mid);

    steps.push({
      array: [...arr],
      description: `배열을 ${JSON.stringify(left)}와(과) ${JSON.stringify(right)}로 나눕니다.`,
      variables: { start, mid },
      activeIndices: [...Array(arr.length).keys()].map(i => start + i)
    });

    return merge(mergeSortHelper(left, start), mergeSortHelper(right, start + mid), start);
  };

  const result = mergeSortHelper(arr);

  steps.push({
    array: result,
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};

export const heapSort = (arr) => {
  const steps = [];

  const heapify = (n, i) => {
    let largest = i;
    const left = 2 * i + 1;
    const right = 2 * i + 2;

    steps.push({
      array: [...arr],
      description: `힙을 구성합니다. 현재 노드: ${arr[i]}, 왼쪽 자식: ${arr[left]}, 오른쪽 자식: ${arr[right]}`,
      variables: { i, largest, left, right },
      activeIndices: [i, left, right].filter(idx => idx < n)
    });

    if (left < n && arr[left] > arr[largest]) {
      largest = left;
    }

    if (right < n && arr[right] > arr[largest]) {
      largest = right;
    }

    if (largest !== i) {
      [arr[i], arr[largest]] = [arr[largest], arr[i]];

      steps.push({
        array: [...arr],
        description: `${arr[i]}와(과) ${arr[largest]}을(를) 교환했습니다.`,
        variables: { i, largest },
        activeIndices: [i, largest]
      });

      heapify(n, largest);
    }
  };

  const n = arr.length;

  for (let i = Math.floor(n / 2) - 1; i >= 0; i--) {
    heapify(n, i);
  }

  for (let i = n - 1; i > 0; i--) {
    [arr[0], arr[i]] = [arr[i], arr[0]];

    steps.push({
      array: [...arr],
      description: `최대값 ${arr[i]}을(를) 배열의 끝으로 이동시킵니다.`,
      variables: { i },
      activeIndices: [0, i]
    });

    heapify(i, 0);
  }

  steps.push({
    array: [...arr],
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};

export const quickSort = (arr) => {
  const steps = [];

  const partition = (low, high) => {
    const pivot = arr[high];
    let i = low - 1;

    steps.push({
      array: [...arr],
      description: `피벗으로 ${pivot}을(를) 선택했습니다.`,
      variables: { low, high, pivot },
      activeIndices: [high]
    });

    for (let j = low; j < high; j++) {
      steps.push({
        array: [...arr],
        description: `${arr[j]}와(과) 피벗 ${pivot}을(를) 비교합니다.`,
        variables: { i, j, pivot },
        activeIndices: [j, high]
      });

      if (arr[j] < pivot) {
        i++;
        [arr[i], arr[j]] = [arr[j], arr[i]];

        steps.push({
          array: [...arr],
          description: `${arr[i]}와(과) ${arr[j]}을(를) 교환했습니다.`,
          variables: { i, j },
          activeIndices: [i, j]
        });
      }
    }

    [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]];

    steps.push({
      array: [...arr],
      description: `피벗 ${pivot}을(를) 올바른 위치로 이동시켰습니다.`,
      variables: { i },
      activeIndices: [i + 1, high]
    });

    return i + 1;
  };

  const quickSortHelper = (low, high) => {
    if (low < high) {
      const pi = partition(low, high);

      quickSortHelper(low, pi - 1);
      quickSortHelper(pi + 1, high);
    }
  };

  quickSortHelper(0, arr.length - 1);

  steps.push({
    array: [...arr],
    description: "정렬이 완료되었습니다.",
    variables: {}
  });

  return steps;
};
