import React from 'react';

export const VisualizationArea = ({ state }) => {
  const currentStep = state.steps[state.currentStepIndex];
  return (
    <div className="visualization-area">
      <div className="memory-representation">
        {currentStep?.numbers.map((num, index) => (
          <div
            key={index}
            className={`memory-cell ${index === currentStep.currentIndex ? 'current' : ''}
                       ${index === currentStep.comparingIndex ? 'comparing' : ''}
                       ${currentStep.swappedIndices.includes(index) ? 'swapped' : ''}`}
          >
            {num}
          </div>
        ))}
      </div>
      <div className="variable-state">
        <div>현재 인덱스: {currentStep?.currentIndex}</div>
        <div>비교 인덱스: {currentStep?.comparingIndex}</div>
      </div>
    </div>
  );
};

export const DescriptionArea = ({ description }) => (
  <div className="description-area">
    <p>{description}</p>
  </div>
);

export const ControlPanel = ({ state, setState }) => {
  const goToPreviousStep = () => setState(prevState => ({
    ...prevState,
    currentStepIndex: Math.max(0, prevState.currentStepIndex - 1)
  }));
  const goToNextStep = () => setState(prevState => ({
    ...prevState,
    currentStepIndex: Math.min(prevState.steps.length - 1, prevState.currentStepIndex + 1)
  }));
  const togglePlayPause = () => setState(prevState => ({ ...prevState, isPlaying: !prevState.isPlaying }));
  const changeSpeed = (speed) => setState(prevState => ({ ...prevState, playbackSpeed: speed }));

  return (
    <div className="control-panel">
      <button onClick={goToPreviousStep}>이전</button>
      <button onClick={togglePlayPause}>{state.isPlaying ? '일시정지' : '재생'}</button>
      <button onClick={goToNextStep}>다음</button>
      <input
        type="range"
        min="0.5"
        max="2"
        step="0.5"
        value={state.playbackSpeed}
        onChange={(e) => changeSpeed(parseFloat(e.target.value))}
      />
    </div>
  );
};
