import React, { useState, useEffect } from 'react';
import { Button, Typography, Paper } from '@mui/material';
import { generateSortingSteps } from './SortingAlgorithms';

const SortingVisualizer = ({ numbers, algorithm }) => {
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);

  useEffect(() => {
    setSortingSteps([]);
    setCurrentStep(0);
  }, [numbers, algorithm]);

  const startSorting = async () => {
    try {
      const steps = await generateSortingSteps(algorithm, numbers);
      if (steps && steps.length > 0) {
        setSortingSteps(steps);
        setCurrentStep(0);
      } else {
        console.error('정렬 단계가 비어있습니다.');
        setSortingSteps([]);
      }
    } catch (error) {
      console.error('정렬 중 오류 발생:', error);
      setSortingSteps([]);
    }
  };

  return (
    <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
      <Button variant="contained" color="primary" onClick={startSorting}>
        정렬 시작
      </Button>
      {sortingSteps && sortingSteps.length > 0 ? (
        <>
          <Typography variant="h6" style={{ marginTop: '20px' }}>
            정렬 단계: {currentStep + 1} / {sortingSteps.length}
          </Typography>
          <Typography>{sortingSteps[currentStep].description}</Typography>
          <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
            {sortingSteps[currentStep].numbers.map((num, index) => (
              <div
                key={index}
                style={{
                  width: '30px',
                  height: `${num * 3}px`,
                  backgroundColor: 'blue',
                  margin: '0 2px',
                }}
              />
            ))}
          </div>
          <div style={{ marginTop: '20px' }}>
            <Button
              disabled={currentStep === 0}
              onClick={() => setCurrentStep(currentStep - 1)}
            >
              이전 단계
            </Button>
            <Button
              disabled={currentStep === sortingSteps.length - 1}
              onClick={() => setCurrentStep(currentStep + 1)}
            >
              다음 단계
            </Button>
          </div>
        </>
      ) : (
        <Typography style={{ marginTop: '20px' }}>
          정렬 단계가 없습니다. 정렬을 시작해 주세요.
        </Typography>
      )}
    </Paper>
  );
};

export default SortingVisualizer;
