import React from 'react';
import './SortingVisualizer.css';
import { Paper, Typography, Box } from '@mui/material';

const SortingVisualizer = ({
  algorithm,
  steps,
  currentStep,
}) => {
  const currentStepData = (steps && steps.length > 0)
    ? (currentStep < steps.length ? steps[currentStep] : steps[steps.length - 1])
    : {
        numbers: [],
        stepDescription: '정렬을 시작해 주세요.',
        variables: {},
        activeIndices: []
      };

  return (
    <div className="sorting-visualizer">
      <Typography variant="h5" gutterBottom>
        {algorithm} 시각화 - 단계 {currentStep + 1} / {steps?.length || 1}
      </Typography>

      <Paper elevation={3} className="visualization-container">
        <Box className="array-visualization">
          {currentStepData.numbers && currentStepData.numbers.map((value, idx) => (
            <div
              key={idx}
              className={`array-bar ${currentStepData.activeIndices?.includes(idx) ? 'active' : ''}`}
              style={{
                height: `${value * 3}px`,
                backgroundColor: currentStepData.activeIndices?.includes(idx) ? '#ff6b6b' : '#3498db',
                transition: 'all 0.3s ease'
              }}
            >
              <span className="bar-value">{value}</span>
            </div>
          ))}
        </Box>

        <Box className="step-info" mt={2}>
          <Typography variant="h6">현재 단계 설명</Typography>
          <Paper elevation={1} className="description-box">
            <Typography>{currentStepData.stepDescription}</Typography>
          </Paper>
        </Box>

        <Box className="variables-info" mt={2}>
          <Typography variant="h6">변수 상태</Typography>
          <div className="variables-grid">
            {Object.entries(currentStepData.variables || {}).map(([key, value]) => (
              <Paper key={key} elevation={1} className="variable-card">
                <Typography variant="subtitle2">{key}</Typography>
                <Typography variant="h6">{value}</Typography>
              </Paper>
            ))}
          </div>
        </Box>
      </Paper>
    </div>
  );
};

export default SortingVisualizer;
