import React from 'react';
import './SortingVisualizer.css';

const SortingSteps = {
  '선택 정렬': "배열을 순회하면서 가장 작은 원소를 찾아 맨 앞으로 이동시키는 방식으로 정렬합니다.",
  '삽입 정렬': "배열의 각 원소를 이미 정렬된 부분 배열의 적절한 위치에 삽입하여 정렬합니다.",
  '버블 정렬': "인접한 두 원소를 비교하여 큰 값을 뒤로 보내는 과정을 반복하여 정렬합니다.",
  '병합 정렬': "배열을 반으로 나누고, 각 부분을 정렬한 후 병합하는 과정을 재귀적으로 수행합니다.",
  '힙 정렬': "배열을 최대 힙으로 구성한 후, 루트 노드와 마지막 노드를 교환하며 정렬합니다.",
  '퀵 정렬': "피벗을 선택하고 피벗보다 작은 값과 큰 값을 분할한 후, 각 부분을 재귀적으로 정렬합니다.",
  '트리 정렬': "이진 탐색 트리를 구성한 후, 중위 순회하여 정렬된 결과를 얻습니다.",
  '팀 정렬': "삽입 정렬과 병합 정렬을 조합하여 효율적으로 정렬합니다."
};

const variableDescriptions = {
  i: "현재 기준 인덱스",
  j: "비교 대상 인덱스",
  key: "삽입할 현재 값",
  minIdx: "현재까지의 최소값 인덱스",
  pivot: "퀵정렬의 기준값",
  left: "왼쪽 포인터 또는 왼쪽 부분 배열",
  right: "오른쪽 포인터 또는 오른쪽 부분 배열",
  mid: "중간 인덱스",
  temp: "임시 저장 변수",
  root: "현재 노드의 값",
  low: "현재 부분 배열의 시작 인덱스",
  high: "현재 부분 배열의 끝 인덱스",
  l: "왼쪽 부분 배열의 시작 인덱스",
  m: "중간 인덱스",
  r: "오른쪽 부분 배열의 끝 인덱스",
  RUN: "삽입 정렬을 수행할 부분 배열의 크기"
};

const SortingVisualizer = ({
  algorithm,
  steps,
  currentStep,
  isSorting
}) => {
  const currentStepData = (steps && steps.length > 0)
    ? steps[currentStep]
    : {
        numbers: [],
        stepDescription: '정렬을 시작해 주세요.',
        variables: {},
        activeIndices: []
      };

  const maxValue = Math.max(...(currentStepData.numbers || []));
  const barHeight = 300;

  return (
    <div className="sorting-visualizer">
      <div className="main-panel">
        <h2>{algorithm} 시각화</h2>
        <div className="algorithm-description">
          <p>{SortingSteps[algorithm]}</p>
        </div>
        <div className="visualization-area">
          <div className="array-container">
            {currentStepData.numbers && currentStepData.numbers.map((value, idx) => (
              <div
                key={idx}
                className={`array-bar ${currentStepData.activeIndices?.includes(idx) ? 'active' : ''}`}
                style={{
                  height: `${(value / maxValue) * barHeight}px`,
                  backgroundColor: currentStepData.activeIndices?.includes(idx) ? '#ff6b6b' : '#3498db',
                  transition: 'all 0.3s ease'
                }}
              >
                <span className="bar-value">{value}</span>
              </div>
            ))}
          </div>
          <div className="step-info">
            <h3>현재 단계: {currentStep + 1} / {steps?.length || 1}</h3>
            <div className="step-description">
              {currentStepData.stepDescription}
            </div>
          </div>
          <div className="variables-panel">
            <h3>현재 변수 상태</h3>
            <div className="variables-grid">
              {Object.entries(currentStepData.variables || {}).map(([key, value]) => (
                <div key={key} className="variable-card">
                  <div className="variable-header">
                    <span className="variable-name">{key}</span>
                    <span className="variable-value">{value}</span>
                  </div>
                  <div className="variable-description">{variableDescriptions[key]}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SortingVisualizer;
