import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Grid, Button, Select, MenuItem, Slider } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  paper: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  control: {
    marginBottom: theme.spacing(2),
  },
  arrayContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-end',
    height: '300px',
    marginBottom: theme.spacing(2),
  },
  arrayBar: {
    width: '8px',
    marginRight: '2px',
    backgroundColor: theme.palette.primary.main,
    transition: 'height 0.3s ease',
  },
}));

const App = () => {
  const classes = useStyles();
  const [array, setArray] = useState([]);
  const [algorithm, setAlgorithm] = useState('selection');
  const [SortingStep, setSortingStep] = useState(null);
  const [sorting, setSorting] = useState(false);
  const [speed, setSpeed] = useState(50);

  useEffect(() => {
    generateNewArray();
    fetchSortingStep();
  }, [algorithm]);

  const generateNewArray = () => {
    const newArray = Array.from({ length: 50 }, () => Math.floor(Math.random() * 100) + 1);
    setArray(newArray);
  };

  const fetchSortingStep = async () => {
    const response = await fetch(`/api/sorting/${algorithm}`);
    const data = await response.json();
    setSortingStep(data);
  };

  const startSorting = () => {
    setSorting(true);
    // 여기에 정렬 알고리즘 구현
    // 각 단계마다 setArray를 호출하여 UI 업데이트
  };

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>정렬 알고리즘 시각화</Typography>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={4}>
            <Select
              value={algorithm}
              onChange={(e) => setAlgorithm(e.target.value)}
              fullWidth
              className={classes.control}
            >
              <MenuItem value="selection">선택 정렬</MenuItem>
              <MenuItem value="insertion">삽입 정렬</MenuItem>
              <MenuItem value="bubble">버블 정렬</MenuItem>
              <MenuItem value="merge">병합 정렬</MenuItem>
              <MenuItem value="heap">힙 정렬</MenuItem>
              <MenuItem value="quick">퀵 정렬</MenuItem>
              <MenuItem value="tree">트리 정렬</MenuItem>
              <MenuItem value="tim">팀 정렬</MenuItem>
            </Select>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={generateNewArray}
              fullWidth
              className={classes.control}
            >
              새 배열 생성
            </Button>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Button
              variant="contained"
              color="secondary"
              onClick={startSorting}
              fullWidth
              className={classes.control}
              disabled={sorting}
            >
              정렬 시작
            </Button>
          </Grid>
        </Grid>
        <Typography gutterBottom>속도</Typography>
        <Slider
          value={speed}
          onChange={(e, newValue) => setSpeed(newValue)}
          aria-labelledby="continuous-slider"
        />
      </Paper>
      <div className={classes.arrayContainer}>
        {array.map((value, index) => (
          <div
            key={index}
            className={classes.arrayBar}
            style={{ height: `${value * 3}px` }}
          />
        ))}
      </div>
      {SortingStep && (
        <Paper className={classes.paper}>
          <Typography variant="h6">{SortingStep.name}</Typography>
          <Typography paragraph>{SortingStep.description}</Typography>
          <Typography variant="subtitle1">단계:</Typography>
          <ol>
            {SortingStep.steps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ol>
          <Typography>시간 복잡도: {SortingStep.timeComplexity}</Typography>
          <Typography>공간 복잡도: {SortingStep.spaceComplexity}</Typography>
          <Typography>안정성: {SortingStep.stability}</Typography>
        </Paper>
      )}
    </Container>
  );
};

export default App;
