import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Grid, Button, Select, MenuItem, Slider, TextField, Chip, IconButton } from '@mui/material';
import { styled } from '@mui/material/styles';
import ClearIcon from '@mui/icons-material/Clear';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import PauseIcon from '@mui/icons-material/Pause';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import SkipNextIcon from '@mui/icons-material/SkipNext';
import SortingVisualizer from './SortingVisualizer';
import { generateSortingSteps } from './sortingAlgorithms';
import './App.css';

const StyledContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(3),
}));

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
}));

const StyledControl = styled('div')(({ theme }) => ({
  marginBottom: theme.spacing(2),
}));

const StyledChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

const algorithmOptions = [
  '선택 정렬', '삽입 정렬', '버블 정렬', '병합 정렬',
  '힙 정렬', '퀵 정렬', '트리 정렬', '팀 정렬'
];

const SortingSteps = {
  '선택 정렬': "배열을 순회하면서 가장 작은 원소를 찾아 맨 앞으로 이동시키는 방식으로 정렬합니다.",
  '삽입 정렬': "배열의 각 원소를 이미 정렬된 부분 배열의 적절한 위치에 삽입하여 정렬합니다.",
  '버블 정렬': "인접한 두 원소를 비교하여 큰 값을 뒤로 보내는 과정을 반복하여 정렬합니다.",
  '병합 정렬': "배열을 반으로 나누고, 각 부분을 정렬한 후 병합하는 과정을 재귀적으로 수행합니다.",
  '힙 정렬': "배열을 최대 힙으로 구성한 후, 루트 노드와 마지막 노드를 교환하며 정렬합니다.",
  '퀵 정렬': "피벗을 선택하고 피벗보다 작은 값과 큰 값을 분할한 후, 각 부분을 재귀적으로 정렬합니다.",
  '트리 정렬': "이진 검색 트리를 구성하고 중위 순회하여 정렬된 결과를 얻습니다.",
  '팀 정렬': "작은 부분에는 삽입 정렬을, 큰 부분에는 병합 정렬을 사용하는 하이브리드 정렬 알고리즘입니다."
};

const variableDescriptions = {
  i: "현재 기준 인덱스",
  j: "비교 대상 인덱스",
  key: "삽입할 현재 값",
  minIdx: "현재까지의 최소값 인덱스",
  pivot: "퀵정렬의 기준값",
  left: "왼쪽 포인터 또는 왼쪽 부분 배열",
  right: "오른쪽 포인터 또는 오른쪽 부분 배열",
  mid: "중간 인덱스",
  temp: "임시 저장 변수",
  root: "현재 노드의 값",
  low: "현재 부분 배열의 시작 인덱스",
  high: "현재 부분 배열의 끝 인덱스",
  l: "왼쪽 부분 배열의 시작 인덱스",
  m: "중간 인덱스",
  r: "오른쪽 부분 배열의 끝 인덱스",
  RUN: "삽입 정렬을 수행할 부분 배열의 크기"
};

const App = () => {
  const [array, setArray] = useState([]);
  const [algorithm, setAlgorithm] = useState('선택 정렬');
  const [steps, setSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [isSorting, setIsSorting] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [speed, setSpeed] = useState(50);
  const [inputNumber, setInputNumber] = useState('');

  useEffect(() => {
    if (isSorting && !isPaused && currentStep < steps.length) {
      const timer = setTimeout(() => {
        setCurrentStep(currentStep + 1);
      }, 1000 - speed * 10);
      return () => clearTimeout(timer);
    } else if (currentStep >= steps.length) {
      setIsSorting(false);
      setIsPaused(false);
    }
  }, [isSorting, isPaused, currentStep, steps, speed]);

  const addNumber = () => {
    if (inputNumber && !isNaN(inputNumber)) {
      setArray([...array, parseInt(inputNumber)]);
      setInputNumber('');
    }
  };

  const addRandomNumber = () => {
    const randomNumber = Math.floor(Math.random() * 100) + 1;
    setArray([...array, randomNumber]);
  };

  const removeNumber = (index) => {
    const newArray = [...array];
    newArray.splice(index, 1);
    setArray(newArray);
  };

  const startSorting = () => {
    const sortingSteps = generateSortingSteps(algorithm, array);
    setSteps(sortingSteps);
    setIsSorting(true);
    setIsPaused(false);
    setCurrentStep(0);
  };

  const resetSorting = () => {
    setIsSorting(false);
    setIsPaused(false);
    setCurrentStep(0);
    setArray([]);
  };

  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  const stepForward = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const stepBackward = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <StyledContainer>
      <Typography variant="h4" gutterBottom>정렬 알고리즘 시각화</Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <StyledPaper>
            <Typography variant="h6" gutterBottom>컨트롤</Typography>
            <StyledControl>
              <Select
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
                fullWidth
              >
                {algorithmOptions.map((option) => (
                  <MenuItem key={option} value={option}>{option}</MenuItem>
                ))}
              </Select>
            </StyledControl>
            <StyledControl>
              <TextField
                value={inputNumber}
                onChange={(e) => setInputNumber(e.target.value)}
                type="number"
                label="숫자 입력"
                variant="outlined"
                size="small"
                fullWidth
              />
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={addNumber}
                disabled={isSorting}
                fullWidth
              >
                숫자 추가
              </Button>
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={addRandomNumber}
                disabled={isSorting}
                fullWidth
              >
                랜덤 숫자 추가
              </Button>
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="secondary"
                onClick={startSorting}
                fullWidth
                disabled={isSorting || array.length === 0}
              >
                정렬 시작
              </Button>
            </StyledControl>
            {isSorting && (
              <StyledControl>
                <Grid container spacing={1} justifyContent="center">
                  <Grid item>
                    <IconButton onClick={stepBackward} disabled={currentStep === 0}>
                      <SkipPreviousIcon />
                    </IconButton>
                  </Grid>
                  <Grid item>
                    <IconButton onClick={togglePause}>
                      {isPaused ? <PlayArrowIcon /> : <PauseIcon />}
                    </IconButton>
                  </Grid>
                  <Grid item>
                    <IconButton onClick={stepForward} disabled={currentStep === steps.length - 1}>
                      <SkipNextIcon />
                    </IconButton>
                  </Grid>
                </Grid>
              </StyledControl>
            )}
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={resetSorting}
                fullWidth
              >
                리셋
              </Button>
            </StyledControl>
            <Typography gutterBottom>속도</Typography>
            <Slider
              value={speed}
              onChange={(e, newValue) => setSpeed(newValue)}
              aria-labelledby="continuous-slider"
            />
            <Typography variant="h6" gutterBottom style={{ marginTop: '20px' }}>현재 배열:</Typography>
            <div>
              {array.map((num, index) => (
                <StyledChip
                  key={index}
                  label={num}
                  onDelete={() => removeNumber(index)}
                  disabled={isSorting}
                  deleteIcon={<ClearIcon />}
                />
              ))}
            </div>
          </StyledPaper>
        </Grid>
        <Grid item xs={12} md={8}>
          <StyledPaper>
            <SortingVisualizer
              algorithm={algorithm}
              steps={steps}
              currentStep={currentStep}
              SortingStep={SortingSteps[algorithm]}
              variableDescriptions={variableDescriptions}
            />
          </StyledPaper>
        </Grid>
      </Grid>
    </StyledContainer>
  );
};

export default App;
