import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Grid, Button, Select, MenuItem, Slider, TextField, Chip, IconButton } from '@mui/material';
import { styled } from '@mui/material/styles';
import ClearIcon from '@mui/icons-material/Clear';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import PauseIcon from '@mui/icons-material/Pause';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import SkipNextIcon from '@mui/icons-material/SkipNext';
import SortingVisualizer from './SortingVisualizer';
import axios from 'axios';
import './App.css';

const StyledContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(3),
}));

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
}));

const StyledControl = styled('div')(({ theme }) => ({
  marginBottom: theme.spacing(2),
}));

const StyledChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

const algorithmOptions = [
  '선택정렬', '삽입정렬', '버블정렬', '병합정렬',
  '퀵정렬', '트리정렬'
];

const generateSortingSteps = async (algorithm, arr) => {
  try {
    const response = await axios.post(`/api/sorting/${algorithm}`, arr);
    return response.data;
  } catch (error) {
    console.error('Error generating sorting steps:', error);
    if (error.response) {
      console.error('Error data:', error.response.data);
      console.error('Error status:', error.response.status);
    }
    throw error;
  }
};

const App = () => {
  const [algorithm, setAlgorithm] = useState('');
  const [array, setArray] = useState([]);
  const [steps, setSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [isSorting, setIsSorting] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [speed, setSpeed] = useState(500); // 기본 속도 500ms

  useEffect(() => {
    let timer;
    if (isPlaying && currentStep < steps.length - 1) {
      timer = setTimeout(() => {
        setCurrentStep(prev => prev + 1);
      }, speed);
    } else if (currentStep >= steps.length - 1) {
      setIsPlaying(false);
    }
    return () => clearTimeout(timer);
  }, [isPlaying, currentStep, steps, speed]);

  const startSorting = async () => {
    if (array.length < 2) {
      alert('최소 2개 이상의 숫자를 입력해주세요.');
      return;
    }
    setIsSorting(true);
    try {
      const response = await axios.post('/api/sort', {
        algorithm: algorithm,
        numbers: array
      });
      setSteps(response.data);
      setCurrentStep(0);
      setIsPlaying(true);
    } catch (error) {
      console.error('정렬 중 오류 발생:', error);
      alert('정렬 중 오류가 발생했습니다.');
    }
  };

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  const stepForward = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const stepBackward = () => {
    if (currentStep > 0) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const resetSort = () => {
    setCurrentStep(0);
    setIsPlaying(false);
    setIsSorting(false);
    setSteps([]);
  };

  return (
    <StyledContainer>
      <Typography variant="h4" gutterBottom>정렬 알고리즘 시각화</Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <StyledPaper>
            <Typography variant="h6" gutterBottom>컨트롤</Typography>
            <StyledControl>
              <Select
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
                fullWidth
              >
                {algorithmOptions.map((option) => (
                  <MenuItem key={option} value={option}>{option}</MenuItem>
                ))}
              </Select>
            </StyledControl>
            <StyledControl>
              <TextField
                value={inputNumber}
                onChange={(e) => setInputNumber(e.target.value)}
                type="number"
                label="숫자 입력"
                variant="outlined"
                size="small"
                fullWidth
              />
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={addNumber}
                disabled={isSorting}
                fullWidth
              >
                숫자 추가
              </Button>
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={addRandomNumber}
                disabled={isSorting}
                fullWidth
              >
                랜덤 숫자 추가
              </Button>
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="secondary"
                onClick={startSorting}
                fullWidth
                disabled={isSorting || array.length === 0}
              >
                정렬 시작
              </Button>
            </StyledControl>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={resetSort}
                fullWidth
              >
                리셋
              </Button>
            </StyledControl>
            <Typography gutterBottom>속도: {speed}</Typography>
            <Slider
              value={speed}
              min={100}
              max={2000}
              step={100}
              onChange={(_, newValue) => setSpeed(newValue)}
              valueLabelDisplay="auto"
              valueLabelFormat={value => `${value}ms`}
            />
            <Typography variant="h6" gutterBottom style={{ marginTop: '20px' }}>현재 배열:</Typography>
            <div>
              {array.map((num, index) => (
                <StyledChip
                  key={index}
                  label={num}
                  onDelete={() => removeNumber(index)}
                  disabled={isSorting}
                  deleteIcon={<ClearIcon />}
                />
              ))}
            </div>
          </StyledPaper>
          <StyledPaper>
            <Typography variant="h6" gutterBottom>재생 컨트롤</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
              <IconButton onClick={stepBackward} disabled={!steps.length || currentStep === 0}>
                <SkipPreviousIcon />
              </IconButton>
              <IconButton onClick={togglePlay} disabled={!steps.length || currentStep >= steps.length - 1}>
                {isPlaying ? <PauseIcon /> : <PlayArrowIcon />}
              </IconButton>
              <IconButton onClick={stepForward} disabled={!steps.length || currentStep >= steps.length - 1}>
                <SkipNextIcon />
              </IconButton>
            </div>
            <Typography gutterBottom>속도 조절</Typography>
            <Slider
              value={speed}
              min={100}
              max={2000}
              step={100}
              onChange={(_, newValue) => setSpeed(newValue)}
              valueLabelDisplay="auto"
              valueLabelFormat={value => `${value}ms`}
            />
          </StyledPaper>
        </Grid>
        <Grid item xs={12} md={8}>
          <StyledPaper>
            <SortingVisualizer
              algorithm={algorithm}
              steps={steps}
              currentStep={currentStep}
            />
          </StyledPaper>
        </Grid>
      </Grid>
    </StyledContainer>
  );
};

export default App;
