import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [algorithm, setAlgorithm] = useState('selection');
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);

  const algorithms = ['selection', 'insertion', 'bubble', 'merge', 'heap', 'quick', 'tree', 'tim'];

  const addNumber = () => {
    const newNumber = Math.floor(Math.random() * 100) + 1;
    setNumbers([...numbers, newNumber]);
  };

  const removeNumber = (index) => {
    setNumbers(numbers.filter((_, i) => i !== index));
  };

  const startSorting = async () => {
    try {
      const response = await axios.post(`/proxy/api/sorting/${algorithm}`, numbers);
      setSortingSteps(response.data);
      setCurrentStep(0);
    } catch (error) {
      console.error('Sorting error:', error);
      alert('정렬 중 오류가 발생했습니다: ' + error.message);
    }
  };

  const nextStep = () => {
    if (currentStep < sortingSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="App">
      <h1>정렬 알고리즘 시각화</h1>
      <div>
        <select value={algorithm} onChange={(e) => setAlgorithm(e.target.value)}>
          {algorithms.map(algo => (
            <option key={algo} value={algo}>{algo} 정렬</option>
          ))}
        </select>
        <button onClick={addNumber}>숫자 추가</button>
        <button onClick={startSorting}>정렬 시작</button>
      </div>
      <div className="number-list">
        {numbers.map((num, index) => (
          <div key={index} className="number-item">
            {num}
            <button onClick={() => removeNumber(index)}>X</button>
          </div>
        ))}
      </div>
      {sortingSteps.length > 0 && (
        <div className="sorting-visualization">
          <h2>정렬 과정</h2>
          <div className="step-controls">
            <button onClick={prevStep} disabled={currentStep === 0}>이전</button>
            <span>Step {currentStep + 1} / {sortingSteps.length}</span>
            <button onClick={nextStep} disabled={currentStep === sortingSteps.length - 1}>다음</button>
          </div>
          <div className="step-display">
            {sortingSteps[currentStep].map((num, index) => (
              <div key={index} className="number-item">{num}</div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
