import React, { useState, useEffect } from 'react';
import AlgorithmSelector from './AlgorithmSelector';
import SortingVisualizer from './SortingVisualizer';
import './App.css';

const variableDescriptions = {
  i: "현재 기준 인덱스",
  j: "비교 대상 인덱스",
  key: "삽입할 현재 값",
  minIdx: "현재까지의 최소값 인덱스",
  pivot: "퀵정렬의 기준값",
  left: "왼쪽 포인터",
  right: "오른쪽 포인터",
  mid: "중간 인덱스",
  temp: "임시 저장 변수",
  len1: "왼쪽 부분 배열의 길이",
  len2: "오른쪽 부분 배열의 길이",
  k: "병합 시 현재 위치",
  x: "임시 인덱스",
  size: "현재 부분 배열의 크기",
  start: "현재 부분 배열의 시작 인덱스",
  end: "현재 부분 배열의 끝 인덱스",
  RUN: "팀정렬의 초기 런 크기"
};

const SortingSteps = {
  '선택 정렬': "배열을 순회하면서 가장 작은 원소를 찾아 맨 앞으로 이동시키는 방식으로 정렬합니다.",
  '삽입 정렬': "배열의 각 원소를 이미 정렬된 부분 배열의 적절한 위치에 삽입하여 정렬합니다.",
  '버블 정렬': "인접한 두 원소를 비교하여 큰 값을 뒤로 보내는 과정을 반복하여 정렬합니다.",
  '병합 정렬': "배열을 반으로 나누고, 각 부분을 정렬한 후 병합하는 과정을 재귀적으로 수행합니다.",
  '힙 정렬': "배열을 최대 힙으로 구성한 후, 루트 노드와 마지막 노드를 교환하며 정렬합니다.",
  '퀵 정렬': "피벗을 선택하고 피벗보다 작은 값과 큰 값을 분할한 후, 각 부분을 재귀적으로 정렬합니다.",
  '트리 정렬': "이진 탐색 트리를 구성하고 중위 순회하여 정렬된 결과를 얻습니다.",
  '팀 정렬': "작은 부분 배열에는 삽입 정렬을, 큰 부분 배열에는 병합 정렬을 사용하는 하이브리드 정렬 알고리즘입니다."
};

const App = () => {
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
  const [showPopup, setShowPopup] = useState(true);
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [numbers, setNumbers] = useState([]);

  useEffect(() => {
    if (selectedAlgorithm && numbers.length > 0) {
      const steps = generateSortingSteps(selectedAlgorithm, [...numbers]);
      setSortingSteps(steps);
      setCurrentStep(0);
    }
  }, [selectedAlgorithm, numbers]);

  const generateSortingSteps = (algorithm, arr) => {
    // 여기에 정렬 알고리즘 구현
    // 예시로 빈 배열을 반환합니다. 실제 구현에서는 정렬 단계를 반환해야 합니다.
    return [];
  };

  const addNumber = (num) => {
    setNumbers(prev => [...prev, num]);
  };

  const addRandomNumber = () => {
    const randomNum = Math.floor(Math.random() * 100) + 1;
    addNumber(randomNum);
  };

  const removeNumber = (index) => {
    setNumbers(prev => prev.filter((_, i) => i !== index));
  };

  const resetAlgorithm = () => {
    setShowPopup(true);
    setSelectedAlgorithm('');
    setSortingSteps([]);
    setCurrentStep(0);
  };

  return (
    <div className="app">
      {showPopup && (
        <AlgorithmSelector
          onSelect={(algorithm) => {
            setSelectedAlgorithm(algorithm);
            setShowPopup(false);
          }}
        />
      )}
      {!showPopup && (
        <>
          <div className="number-input">
            <input
              type="number"
              placeholder="숫자 입력"
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  addNumber(parseInt(e.target.value));
                  e.target.value = '';
                }
              }}
            />
            <button onClick={addRandomNumber}>랜덤 숫자 추가</button>
          </div>
          <div className="current-numbers">
            현재 숫자:
            {numbers.map((num, index) => (
              <span key={index} className="number-tag">
                {num}
                <button onClick={() => removeNumber(index)}>×</button>
              </span>
            ))}
          </div>
          <SortingVisualizer
            algorithm={selectedAlgorithm}
            steps={sortingSteps}
            currentStep={currentStep}
            setCurrentStep={setCurrentStep}
            variableDescriptions={variableDescriptions}
            SortingSteps={SortingSteps}
          />
          <button className="reset-button" onClick={resetAlgorithm}>정렬 다시 선택</button>
        </>
      )}
    </div>
  );
};

export default App;
