import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import SortingVisualizer from './SortingVisualizer';
import AlgorithmSelector from './AlgorithmSelector';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [algorithm, setAlgorithm] = useState('');
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [showSelector, setShowSelector] = useState(false);

  const addNumber = () => {
    const newNumber = Math.floor(Math.random() * 100) + 1;
    setNumbers([...numbers, newNumber]);
  };

  const removeNumber = (index) => {
    setNumbers(numbers.filter((_, i) => i !== index));
  };

  const startSorting = async () => {
    if (!algorithm) {
      alert('정렬 알고리즘을 선택해주세요.');
      return;
    }
    try {
      const response = await axios.post(`/proxy/api/sorting/${algorithm}`, numbers);
      setSortingSteps(response.data);
      setCurrentStep(0);
    } catch (error) {
      console.error('Sorting error:', error);
      alert('정렬 중 오류가 발생했습니다: ' + error.message);
    }
  };

  const selectAlgorithm = (selectedAlgorithm) => {
    setAlgorithm(selectedAlgorithm);
    setShowSelector(false);
  };

  return (
    <div className="App">
      <h1>정렬 알고리즘 시각화</h1>
      <div className="controls">
        <button onClick={addNumber}>숫자 추가</button>
        <button onClick={() => setShowSelector(true)}>
          {algorithm ? `${algorithm} 정렬 변경` : '정렬 방식 선택'}
        </button>
        <button onClick={startSorting} disabled={!algorithm || numbers.length === 0}>
          정렬 시작
        </button>
      </div>
      <div className="number-list">
        {numbers.map((num, index) => (
          <div key={index} className="number-item">
            {num}
            <button onClick={() => removeNumber(index)} className="remove-btn">X</button>
          </div>
        ))}
      </div>
      {sortingSteps.length > 0 && (
        <SortingVisualizer
          steps={sortingSteps}
          currentStep={currentStep}
          setCurrentStep={setCurrentStep}
        />
      )}
      {showSelector && (
        <AlgorithmSelector
          onSelect={selectAlgorithm}
          onClose={() => setShowSelector(false)}
        />
      )}
    </div>
  );
}

export default App;
