import React from 'react';
import './SortingVisualizer.css';

function SortingVisualizer({ steps, currentStep, setCurrentStep }) {
  const nextStep = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="sorting-visualizer">
      <h2>정렬 과정</h2>
      <div className="step-controls">
        <button onClick={prevStep} disabled={currentStep === 0}>이전</button>
        <span>Step {currentStep + 1} / {steps.length}</span>
        <button onClick={nextStep} disabled={currentStep === steps.length - 1}>다음</button>
      </div>
      <div className="step-display">
        {steps[currentStep].numbers.map((num, index) => (
          <div
            key={index}
            className="number-bar"
            style={{ height: `${num * 3}px` }}
          >
            {num}
          </div>
        ))}
      </div>
      <div className="step-description">
        {steps[currentStep].description}
      </div>
    </div>
  );
}

export default SortingVisualizer;
