import React, { useState, useEffect } from 'react';
import { AlgorithmSelector, NumberInput } from './InputComponents';
import { VisualizationArea, DescriptionArea, ControlPanel } from './VisualizationComponents';
import './App.css';

const App = () => {
  const [algorithmState, setAlgorithmState] = useState({
    steps: [],
    currentStepIndex: 0,
    isPlaying: false,
    playbackSpeed: 1,
  });
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
  const [inputNumbers, setInputNumbers] = useState([]);

  useEffect(() => {
    let interval;
    if (algorithmState.isPlaying) {
      interval = setInterval(() => {
        setAlgorithmState(prevState => ({
          ...prevState,
          currentStepIndex: Math.min(prevState.currentStepIndex + 1, prevState.steps.length - 1)
        }));
      }, 1000 / algorithmState.playbackSpeed);
    }
    return () => clearInterval(interval);
  }, [algorithmState.isPlaying, algorithmState.playbackSpeed]);

  const startSorting = async () => {
    try {
      const response = await fetch('/api/sort', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ algorithm: selectedAlgorithm, numbers: inputNumbers }),
      });
      const data = await response.json();
      setAlgorithmState(prevState => ({
        ...prevState,
        steps: data,
        currentStepIndex: 0,
        isPlaying: false,
      }));
    } catch (error) {
      console.error('Error fetching sorting steps:', error);
    }
  };

  return (
    <div className="app">
      <div className="header">
        <AlgorithmSelector onSelect={setSelectedAlgorithm} />
        <NumberInput onSubmit={setInputNumbers} />
        <button onClick={startSorting}>시작</button>
      </div>
      <VisualizationArea state={algorithmState} />
      <DescriptionArea description={algorithmState.steps[algorithmState.currentStepIndex]?.description} />
      <ControlPanel state={algorithmState} setState={setAlgorithmState} />
    </div>
  );
};

export default App;
