import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Grid, Button, Select, MenuItem, Slider } from '@mui/material';
import { styled } from '@mui/material/styles';
import SortingVisualizer from './SortingVisualizer';
import { generateSortingSteps } from './sortingAlgorithms';
import './App.css';

const StyledContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(3),
}));

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
}));

const StyledControl = styled('div')(({ theme }) => ({
  marginBottom: theme.spacing(2),
}));

const algorithmOptions = [
  '선택 정렬', '삽입 정렬', '버블 정렬', '병합 정렬',
  '힙 정렬', '퀵 정렬'
];

const SortingSteps = {
  '선택 정렬': "배열을 순회하면서 가장 작은 원소를 찾아 맨 앞으로 이동시키는 방식으로 정렬합니다.",
  '삽입 정렬': "배열의 각 원소를 이미 정렬된 부분 배열의 적절한 위치에 삽입하여 정렬합니다.",
  '버블 정렬': "인접한 두 원소를 비교하여 큰 값을 뒤로 보내는 과정을 반복하여 정렬합니다.",
  '병합 정렬': "배열을 반으로 나누고, 각 부분을 정렬한 후 병합하는 과정을 재귀적으로 수행합니다.",
  '힙 정렬': "배열을 최대 힙으로 구성한 후, 루트 노드와 마지막 노드를 교환하며 정렬합니다.",
  '퀵 정렬': "피벗을 선택하고 피벗보다 작은 값과 큰 값을 분할한 후, 각 부분을 재귀적으로 정렬합니다."
};

const variableDescriptions = {
  i: "현재 기준 인덱스",
  j: "비교 대상 인덱스",
  key: "삽입할 현재 값",
  minIdx: "현재까지의 최소값 인덱스",
  pivot: "퀵정렬의 기준값",
  left: "왼쪽 포인터",
  right: "오른쪽 포인터",
  mid: "중간 인덱스",
  temp: "임시 저장 변수"
};

const App = () => {
  const [array, setArray] = useState([]);
  const [algorithm, setAlgorithm] = useState('선택 정렬');
  const [steps, setSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [isSorting, setIsSorting] = useState(false);
  const [speed, setSpeed] = useState(50);

  useEffect(() => {
    generateNewArray();
  }, []);

  const generateNewArray = () => {
    const newArray = Array.from({length: 50}, () => Math.floor(Math.random() * 100) + 1);
    setArray(newArray);
    setSteps([]);
    setCurrentStep(0);
  };

  const startSorting = () => {
    const sortingSteps = generateSortingSteps(algorithm, array);
    setSteps(sortingSteps);
    setIsSorting(true);
    setCurrentStep(0);
  };

  const resetSorting = () => {
    setIsSorting(false);
    setCurrentStep(0);
  };

  return (
    <StyledContainer>
      <Typography variant="h4" gutterBottom>정렬 알고리즘 시각화</Typography>
      <StyledPaper>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={4}>
            <StyledControl>
              <Select
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
                fullWidth
              >
                {algorithmOptions.map((option) => (
                  <MenuItem key={option} value={option}>{option}</MenuItem>
                ))}
              </Select>
            </StyledControl>
          </Grid>
          <Grid item xs={12} sm={4}>
            <StyledControl>
              <Button
                variant="contained"
                color="primary"
                onClick={generateNewArray}
                fullWidth
                disabled={isSorting}
              >
                새 배열 생성
              </Button>
            </StyledControl>
          </Grid>
          <Grid item xs={12} sm={4}>
            <StyledControl>
              <Button
                variant="contained"
                color="secondary"
                onClick={startSorting}
                fullWidth
                disabled={isSorting}
              >
                정렬 시작
              </Button>
            </StyledControl>
          </Grid>
        </Grid>
        <Typography gutterBottom>속도</Typography>
        <Slider
          value={speed}
          onChange={(e, newValue) => setSpeed(101 - newValue)}
          aria-labelledby="continuous-slider"
        />
      </StyledPaper>
      <StyledPaper>
        <Typography variant="h6" gutterBottom>알고리즘 설명:</Typography>
        <Typography>{SortingSteps[algorithm]}</Typography>
      </StyledPaper>
      <SortingVisualizer
        algorithm={algorithm}
        steps={steps}
        currentStep={currentStep}
        setCurrentStep={setCurrentStep}
        SortingStep={SortingSteps[algorithm]}
        variableDescriptions={variableDescriptions}
      />
    </StyledContainer>
  );
};

export default App;
