import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [algorithm, setAlgorithm] = useState('selection');
  const [numbers, setNumbers] = useState([]);
  const [currentNumber, setCurrentNumber] = useState('');
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);

  const addNumber = () => {
    if (currentNumber && !isNaN(currentNumber)) {
      setNumbers([...numbers, parseInt(currentNumber)]);
      setCurrentNumber('');
    }
  };

  const removeNumber = (index) => {
    setNumbers(numbers.filter((_, i) => i !== index));
  };

  const startSorting = async () => {
    try {
      const response = await axios.post(`/api/sorting/${algorithm}`, numbers);
      setSortingSteps(response.data);
      setCurrentStep(0);
    } catch (error) {
      console.error('Sorting error:', error);
    }
  };

  const nextStep = () => {
    if (currentStep < sortingSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="App">
      <h1>정렬 알고리즘 시각화</h1>
      <select value={algorithm} onChange={(e) => setAlgorithm(e.target.value)}>
        <option value="selection">선택 정렬</option>
        <option value="insertion">삽입 정렬</option>
        {/* 다른 정렬 알고리즘 옵션들 추가 */}
      </select>
      <div>
        <input
          type="number"
          value={currentNumber}
          onChange={(e) => setCurrentNumber(e.target.value)}
        />
        <button onClick={addNumber}>숫자 추가</button>
      </div>
      <div>
        {numbers.map((num, index) => (
          <span key={index} onClick={() => removeNumber(index)}>
            {num}{' '}
          </span>
        ))}
      </div>
      <button onClick={startSorting}>정렬 시작</button>
      <div>
        {sortingSteps[currentStep] && (
          <div>
            {sortingSteps[currentStep].map((num, index) => (
              <span key={index}>{num} </span>
            ))}
          </div>
        )}
      </div>
      <button onClick={prevStep} disabled={currentStep === 0}>
        이전 단계
      </button>
      <button onClick={nextStep} disabled={currentStep === sortingSteps.length - 1}>
        다음 단계
      </button>
    </div>
  );
}

export default App;
