import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [algorithm, setAlgorithm] = useState('');
  const [numbers, setNumbers] = useState([]);
  const [currentNumber, setCurrentNumber] = useState('');
  const [sortingSteps, setSortingSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [showModal, setShowModal] = useState(true);

  useEffect(() => {
    if (algorithm) {
      const initialArray = Array.from({length: 10}, () => Math.floor(Math.random() * 100));
      setNumbers(initialArray);
      setSortingSteps([initialArray]);
    }
  }, [algorithm]);

  const addNumber = () => {
    if (currentNumber && !isNaN(currentNumber)) {
      const newNumbers = [...numbers, parseInt(currentNumber)];
      setNumbers(newNumbers);
      setSortingSteps([newNumbers]);
      setCurrentNumber('');
      setCurrentStep(0);
    }
  };

  const removeNumber = (index) => {
    const newNumbers = numbers.filter((_, i) => i !== index);
    setNumbers(newNumbers);
    setSortingSteps([newNumbers]);
    setCurrentStep(0);
  };

     const startSorting = async () => {
       try {
         console.log('Sorting started:', algorithm, numbers);
         const response = await axios.post(`/api/sorting/${algorithm}`, numbers);
         console.log('Sorting response:', response.data);
         setSortingSteps(response.data);
         setCurrentStep(0);
       } catch (error) {
         console.error('Sorting error:', error);
         if (error.response) {
           console.error('Error data:', error.response.data);
           console.error('Error status:', error.response.status);
           console.error('Error headers:', error.response.headers);
         }
         alert('정렬 중 오류가 발생했습니다: ' + error.message);
       }
     };

  const nextStep = () => {
    if (currentStep < sortingSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const resetAlgorithm = () => {
    setAlgorithm('');
    setNumbers([]);
    setSortingSteps([]);
    setCurrentStep(0);
    setShowModal(true);
  };

  const VisualizationComponent = () => {
    if (!algorithm) return null;

    return (
      <div className="visualization">
        {sortingSteps[currentStep] && sortingSteps[currentStep].map((num, index) => (
          <div
            key={index}
            className="bar"
            style={{height: `${num * 3}px`}}
            onClick={() => removeNumber(index)}
          >
            {num}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="App">
      <h1>정렬 알고리즘 시각화</h1>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>정렬 알고리즘 선택</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Button onClick={() => { setAlgorithm('selection'); setShowModal(false); }}>선택 정렬</Button>
          <Button onClick={() => { setAlgorithm('insertion'); setShowModal(false); }}>삽입 정렬</Button>
          {/* 여기에 다른 정렬 알고리즘 버튼들을 추가할 수 있습니다 */}
        </Modal.Body>
      </Modal>

      {algorithm && (
        <div>
          <h2>{algorithm === 'selection' ? '선택 정렬' : '삽입 정렬'}</h2>
          <VisualizationComponent />
          <div>
            <input
              type="number"
              value={currentNumber}
              onChange={(e) => setCurrentNumber(e.target.value)}
            />
            <Button onClick={addNumber}>숫자 추가</Button>
          </div>
          <Button onClick={startSorting}>정렬 시작</Button>
          <Button onClick={prevStep} disabled={currentStep === 0}>이전 단계</Button>
          <Button onClick={nextStep} disabled={currentStep === sortingSteps.length - 1}>다음 단계</Button>
          <div>현재 단계: {currentStep + 1} / {sortingSteps.length}</div>
          <Button onClick={resetAlgorithm}>다시 정렬 선택</Button>
        </div>
      )}
    </div>
  );
}

export default App;
